mapper.select:
SELECT tp.id, tp.type_id, tpt.name as type_name, tp.parent_id, tp.type, tp.op_type, tp.name, tp.code, tp.resource, tp.resource_interface, tp.level, tp.remark, tp.del_flag, tp.create_time, tp.update_time, tp.create_by, tp.update_by FROM tb_privilege tp LEFT JOIN tb_privilege_type tpt ON tpt.id = tp.type_id
tp.del_flag = 0
tp.id=#{query.id}
and tp.parent_id=#{query.parentId}
and tp.name like CONCAT('%',#{query.name},'%')
and tp.code=#{query.code}
and tp.level=#{query.level}
and tp.type=#{query.type}
and tp.type=#{query.opType}
and tp.create_by=#{query.createBy}
and tp.update_by=#{query.updateBy}
and tp.type_id = #{query.typeId}
order by tp.update_time desc

        SELECT
			distinct tp.id,
			tp.type_id,
			tp.parent_id,
			tp.name,
			tp.code,
			tp.type,
			tp.op_type,
			tp.resource,
			tp.resource_interface,
			tp.level,
			tp.remark,
			tp.del_flag,
			tp.create_time,
			tp.update_time,
			tp.create_by,
			tp.update_by
		FROM
			tb_privilege tp
		WHERE
			tp.del_flag = 0
			
				and tp.id=#{query.id}
			
			
				and tp.type_id = #{query.typeId}
			
			
				and tp.parent_id=#{query.parentId}
			
			
				and tp.name like CONCAT('%',#{query.name},'%')
			
			
				and tp.resource = #{query.resource}
			
			
				and tp.code=#{query.code}
			
			
				and tp.level=#{query.level}
			
			
				and tp.type=#{query.type}
			
			
				and tp.op_type=#{query.opType}
			
			
				and tp.del_flag=#{query.delFlag}
			
			
				and tp.create_by=#{query.createBy}
			
			
				and tp.update_by=#{query.updateBy}

SELECT DISTINCT tp.id, tp.parent_id, tp.NAME, tp.CODE, tp.resource, tp.resource_interface, tp.method, tp.LEVEL, tp.type, tp.op_type, tpt.name as type_name FROM tb_privilege tp INNER JOIN tb_role_privilege tsp ON tp.id = tsp.privilege_id AND tp.del_flag = 0 INNER JOIN tb_role tr ON tsp.role_id = tr.id AND tr.del_flag = 0 INNER JOIN tb_user_role tur ON tr.id = tur.role_id INNER JOIN tb_user tu ON tur.user_id = tu.id AND tu.del_flag = 0 LEFT JOIN tb_privilege_type tpt ON tpt.id = tp.type_id WHERE 1 = 1
and tp.type_id=#{query.typeId}
and tu.id=#{query.userId}
order by tp.id asc

SELECT distinct tp.id, tp.type_id, tpt.name as type_name, tp.parent_id, tp.name, tp.code, tp.type, tp.op_type, tp.resource, tp.resource_interface, tp.level, tp.remark, tp.del_flag, tp.create_time, tp.update_time, tp.create_by, tp.update_by FROM tb_privilege tp WHERE tp.del_flag = 0
and tp.id = #{query.id}
and tp.type_id = #{query.typeId}
and tp.name LIKE CONCAT("%",#{query.name},"%")
and tp.code LIKE CONCAT("%",#{query.code},"%")
and tp.level = #{query.level}
and tp.del_flag = #{query.delFlag}
and tp.create_by = #{query.createBy}

SELECT distinct tp.id, tp.type_id, tp.parent_id, tp.name, tp.type, tp.resource FROM tb_privilege tp WHERE tp.del_flag = 0
and tp.parent_id=#{query.parentId}
and tp.parent_id is null
and tp.name = #{query.name}
and tp.resource = #{query.resource}
and tp.type=#{query.type}
and tp.create_by=#{query.createBy}
