sql.where:

	AND tu.id = #{id}


	AND tu.name = #{name}


	AND tu.display_name = #{displayName}


	AND tu.email = #{email}


	AND tu.password = #{password}


	AND tu.is_enable = #{isEnable}


	AND tu.phone = #{phone}


	AND tu.remark = #{remark}

AND tu.del_flag = 0

mapper.select:
SELECT id, org_id, name, password, email, phone, display_name, create_time, update_time, create_by, update_by, del_flag, is_enable, data_control, expire_time, remark FROM tb_user WHERE del_flag = 0
AND DATE_FORMAT(NOW(),'%Y-%m-%d') > DATE_FORMAT(expire_time,'%Y-%m-%d')
AND is_enable = #{query.isEnable}
