package com.bnmotor.icv.tsp.ota.service.v2;

import com.bnmotor.icv.tsp.ota.common.enums.ApproveStateEnum;
import com.bnmotor.icv.tsp.ota.common.enums.EnableStateEnum;
import com.bnmotor.icv.tsp.ota.common.enums.PlanModeStateEnum;
import com.bnmotor.icv.tsp.ota.common.enums.PublishStateEnum;
import com.bnmotor.icv.tsp.ota.model.entity.FotaPlanPo;
import com.bnmotor.icv.tsp.ota.service.IFotaPlanDbService;
import com.bnmotor.icv.tsp.ota.service.IFotaPlanPublishService;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Component;

import java.time.LocalDateTime;
import java.time.ZoneId;
import java.util.Arrays;
import java.util.Date;
import java.util.List;

/**
 * @ClassName: FotaPlanPublishService.java
 * @Description: 处理任务发布状态、审批状态变更逻辑
 * @author E.YanLonG
 *
 *         <pre>
 * 1、任务名称：用户自定义，例如：GX16 2020年第一次升级任务

2、升级方式：静默下载模式、工厂模式、正常模式

3、整车版本号（目标版本号）：以创建的名称为准，没有则不显示；

4、任务所含车辆数：例：1000个

5、任务审批状态：「免审批」（仅针对测试任务）「待审批」「审批中」「已审批」

6、任务发布状态：「发布中」、「待发布」、「延迟发布」、「暂停」、「已失效」

「发布中」：（1）测试任务完成任务创建&任务启停开关处于ON；（2）正式任务完成任务创建&完成审批&处于任务有效时间内&任务启停开关处于ON；

「待发布」：(1)正式任务完成创建&审批未完成；（2）正式任务完成创建&审批已完成&未到达任务开始时间；

「延迟发布」：正式任务完成创建&审批已完成&已达任务开始时间&任务启停开关处于OFF（该场景应用在任务已审批，但需要临时延迟任务开始时间）

「任务暂停」：任务启停开关处于「OFF」状态，且调整任务在此之前已经处于「发布中」状态。

「已失效」：任务超过有效期。
 *
 *         </pre>
 * 
 *         <img src=
 *         'data:image/png;base64,'></img>
 * @since 2021-1-20 16:12:29
 * @Copyright: 2020 www.baoneng.com Inc. All rights reserved.
 *             注意：本内容仅限于宝能汽车公司内部传阅，禁止外泄以及用于其他的商业目的
 */
@Component
@Slf4j
public class FotaPlanPublishServiceImpl implements IFotaPlanPublishService {

	@Autowired
	IFotaPlanDbService fotaPlanDbService;

	@Override
	public void selectPublishState(FotaPlanPo fotaPlanPo) {

		// 「已失效」：任务超过有效期
		{
			boolean durationExpired = durationExpired(fotaPlanPo);
			if (durationExpired) {
				fotaPlanPo.setPublishState(PublishStateEnum.INVALID.getState()); // 任务已失效
				// fotaPlanPo.setPlanStatus(Enums.PlanStatusTypeEnum.INVALID.getType());
				return;
			}
		}

		PlanModeStateEnum planMode = PlanModeStateEnum.select(fotaPlanPo.getPlanMode());
		ApproveStateEnum approve = ApproveStateEnum.select(fotaPlanPo.getApproveState());
		EnableStateEnum enable = EnableStateEnum.select(fotaPlanPo.getIsEnable());
		PublishStateEnum publish = PublishStateEnum.select(fotaPlanPo.getPublishState());
		DurationEnum duration = duration(fotaPlanPo);
		
		// 「待发布」：(1)正式任务完成创建&审批未完成；
		{
			// 审批状态
			if (PlanModeStateEnum.FORMAL.equals(planMode) && !ApproveStateEnum.BE_APPROVED.equals(approve)) {
				fotaPlanPo.setPublishState(PublishStateEnum.UNPUBLISH.getState()); // 任务待发布
				// fotaPlanPo.setPlanStatus(Enums.PlanStatusTypeEnum.CREATED.getType());
				return;
			}
		}
		// 「待发布」：（2）正式任务完成创建&审批已完成&未到达任务开始时间；
		{
			boolean unreached = durationUnreached(fotaPlanPo);
			if (PlanModeStateEnum.FORMAL.equals(planMode) && ApproveStateEnum.BE_APPROVED.equals(approve) && unreached) {
				fotaPlanPo.setPublishState(PublishStateEnum.UNPUBLISH.getState()); // 任务待发布
				// fotaPlanPo.setPlanStatus(Enums.PlanStatusTypeEnum.AUDITED.getType());
				return;
			}
		}

		// 「待发布」：（2）测试任务&免审批&未到达任务开始时间；
		{
			boolean unreached = durationUnreached(fotaPlanPo);
			if (PlanModeStateEnum.TEST.equals(planMode) && unreached //
//					&& (ApproveStateEnum.BE_APPROVED.equals(approve) || ApproveStateEnum.NO_NEED_APPROVE.equals(approve)) //
					) {
				fotaPlanPo.setPublishState(PublishStateEnum.UNPUBLISH.getState()); // 任务待发布
				// fotaPlanPo.setPlanStatus(Enums.PlanStatusTypeEnum.AUDITED.getType());
				return;
			}
		}

		// 「发布中」：（1）测试任务完成任务创建&任务启停开关处于ON；
		{
			// 开关状态
			// 任务模式 0测试任务 1正式任务
			boolean durationInPerion = durationInPerion(fotaPlanPo);
			if (PlanModeStateEnum.TEST.equals(planMode) && EnableStateEnum.OPEN.equals(enable) && durationInPerion) {
				fotaPlanPo.setPublishState(PublishStateEnum.IN_PUBLISHING.getState()); // 任务发布中
				// fotaPlanPo.setPlanStatus(Enums.PlanStatusTypeEnum.AUDITED.getType());
				return;
			}

		}

		// 「发布中」：（2）正式任务完成任务创建&完成审批&处于任务有效时间内&任务启停开关处于ON；
		{
			boolean durationInPerion = durationInPerion(fotaPlanPo);
			if (PlanModeStateEnum.FORMAL.equals(planMode) && ApproveStateEnum.BE_APPROVED.equals(approve)
					&& EnableStateEnum.OPEN.equals(enable) && durationInPerion) {
				fotaPlanPo.setPublishState(PublishStateEnum.IN_PUBLISHING.getState()); // 任务发布中
				// fotaPlanPo.setPlanStatus(Enums.PlanStatusTypeEnum.AUDITED.getType());
				return;
			}
		}

		// 「任务暂停」：任务启停开关处于「OFF」状态，且调整任务在此之前已经处于「发布中」状态。
		{
			// 开关状态
			if (EnableStateEnum.CLOSE.equals(enable) && PublishStateEnum.IN_PUBLISHING.equals(publish)) {
				fotaPlanPo.setPublishState(PublishStateEnum.SUSPEND.getState()); // 任务暂停
				// fotaPlanPo.setPlanStatus(Enums.PlanStatusTypeEnum.AUDITED.getType());
				return;
			}
		}

		// 「延迟发布」：正式任务完成创建&审批已完成&已达任务开始时间&任务启停开关处于OFF（该场景应用在任务已审批，但需要临时延迟任务开始时间）
		{
			boolean durationInPerion = durationInPerion(fotaPlanPo);
			if (PlanModeStateEnum.FORMAL.equals(planMode) && ApproveStateEnum.BE_APPROVED.equals(approve)
					&& EnableStateEnum.CLOSE.equals(enable) && durationInPerion) {
				fotaPlanPo.setPublishState(PublishStateEnum.DELAY_PUBLISH.getState()); // 任务延迟发布
				/* fotaPlanPo.setPlanStatus(Enums.PlanStatusTypeEnum.AUDITED.getType()); */
				return;
			}
		}

	}

	/**
	 * Date 转换LocalDateTime
	 * 
	 * @param date
	 * @return
	 */
	public LocalDateTime toLocalDateTime(Date date) {
		return LocalDateTime.ofInstant(date.toInstant(), ZoneId.systemDefault());
	}
	
	public DurationEnum duration(FotaPlanPo fotaPlanPo) {
		if (durationExpired(fotaPlanPo)) {
			return DurationEnum.EXPIRED;
		}
		
		if (durationUnreached(fotaPlanPo)) {
			return DurationEnum.UNREACHED;
		}
		
		if (durationExpired(fotaPlanPo)) {
			return DurationEnum.EXPIRED;
		}
		
		return DurationEnum.UNREACHED;
		
	}

	/**
	 * 任务在是否在有效期中
	 */
	public boolean durationInPerion(FotaPlanPo fotaPlanPo) {
		LocalDateTime localDateTime = LocalDateTime.now();

		Date startTime = fotaPlanPo.getPlanStartTime();
		Date endTime = fotaPlanPo.getPlanEndTime();

		LocalDateTime localStartTime = toLocalDateTime(startTime);
		LocalDateTime localEndTime = toLocalDateTime(endTime);
		return localDateTime.isAfter(localStartTime) && localDateTime.isBefore(localEndTime);
	}

	/**
	 * 任务还未开始
	 */
	public boolean durationUnreached(FotaPlanPo fotaPlanPo) {
		LocalDateTime localDateTime = LocalDateTime.now();

		Date startTime = fotaPlanPo.getPlanStartTime();
		Date endTime = fotaPlanPo.getPlanEndTime();

		LocalDateTime localStartTime = toLocalDateTime(startTime);
		LocalDateTime localEndTime = toLocalDateTime(endTime);
		return localDateTime.isBefore(localStartTime) && localDateTime.isBefore(localEndTime);
	}

	/**
	 * 任务已过期
	 * 
	 * @param fotaPlanPo
	 * @return
	 */
	public boolean durationExpired(FotaPlanPo fotaPlanPo) {
		LocalDateTime localDateTime = LocalDateTime.now();

		Date startTime = fotaPlanPo.getPlanStartTime();
		Date endTime = fotaPlanPo.getPlanEndTime();

		LocalDateTime localStartTime = toLocalDateTime(startTime);
		LocalDateTime localEndTime = toLocalDateTime(endTime);
		return localDateTime.isAfter(localStartTime) && localDateTime.isAfter(localEndTime);
	}

	/**
	 * 新建任务时开关应该设置成'关闭' 「待发布」 发布中和待发布才可以关闭 暂停和延迟发布才可以打开
	 */
	@Override
	public boolean editable(Long otaPlanId, Integer isEnable) {
		FotaPlanPo fotaPlanPo = fotaPlanDbService.getById(otaPlanId);
		Integer publishState = fotaPlanPo.getPublishState();
		PublishStateEnum publishStateEnum = PublishStateEnum.select(publishState);
		Integer enableState = fotaPlanPo.getIsEnable();
		EnableStateEnum enableStateEnum = EnableStateEnum.select(enableState);

		// 打开开关
		if (isEnable.intValue() == 1) {
			List<PublishStateEnum> target = Arrays.asList(PublishStateEnum.SUSPEND, PublishStateEnum.DELAY_PUBLISH);
			return target.contains(publishStateEnum) || (EnableStateEnum.CLOSE.equals(enableStateEnum)
					&& PublishStateEnum.UNPUBLISH.equals(publishStateEnum));
		}

		// 关闭开关
		if (isEnable.intValue() == 0) {
			List<PublishStateEnum> target = Arrays.asList(PublishStateEnum.IN_PUBLISHING, PublishStateEnum.UNPUBLISH);
			return target.contains(publishStateEnum);
		}
		return false;
	}

	@Override
	public boolean isEffectiveFotaPlan(FotaPlanPo fotaPlanPo) {
		// 开关状态
		EnableStateEnum enable = EnableStateEnum.select(fotaPlanPo.getIsEnable());
		// 审批状态
		ApproveStateEnum approve = ApproveStateEnum.select(fotaPlanPo.getApproveState());

		boolean durationInPerion = durationInPerion(fotaPlanPo);
		return EnableStateEnum.OPEN.equals(enable) && ApproveStateEnum.approved(approve.getState()) && durationInPerion;
	}

	@Override
	public boolean canDeleteFotaPlan(FotaPlanPo fotaPlanPo) {
		EnableStateEnum enable = EnableStateEnum.select(fotaPlanPo.getIsEnable());
		ApproveStateEnum approve = ApproveStateEnum.select(fotaPlanPo.getApproveState());
		PublishStateEnum publishState = PublishStateEnum.select(fotaPlanPo.getPublishState());
		PlanModeStateEnum planModeState = PlanModeStateEnum.select(fotaPlanPo.getPlanMode());

		// 正常情况下只有开关关闭的任务才能删除
		if (EnableStateEnum.OPEN.equals(enable)) {
			return false;
		}

		// 正式任务
		if (PlanModeStateEnum.FORMAL.equals(planModeState)) {

			// 只要待审批就可以删除
			if (ApproveStateEnum.READY_FOR_APPROVE.equals(approve)) {
				return true;
			}

			if (ApproveStateEnum.BE_APPROVED.equals(approve)) {
				return false;
			}

			// 发布中不能删除
			if (PublishStateEnum.IN_PUBLISHING.equals(publishState)) {
				return false;
			}
			// 暂停中的任务不能删除
			if (PublishStateEnum.SUSPEND.equals(publishState)) {
				return false;
			}
			// 正式任务 失效中不能删除
			if (PublishStateEnum.INVALID.equals(publishState)) {
				return false;
			}
		}

		// 测试任务
		if (PlanModeStateEnum.TEST.equals(planModeState)) {
			if (PublishStateEnum.IN_PUBLISHING.equals(publishState)) {
				return false;
			}

			if (PublishStateEnum.INVALID.equals(publishState)) {
				return false;
			}
		}

		//
		return true;
	}
}

enum DurationEnum {
	UNREACHED, // 未到达预定时间
	INDURATION, // 在预定时间中
	EXPIRED; // 过期
	
	public static boolean isExpired(DurationEnum value) {
		return DurationEnum.EXPIRED.equals(value);
	}
	
	public static boolean isInduration(DurationEnum value) {
		return DurationEnum.INDURATION.equals(value);
	}
	
	public static boolean isUnreached(DurationEnum value) {
		return DurationEnum.UNREACHED.equals(value);
	}
}